mapper.select:
select num, title, name, writedate, rn
from(select num, title, name, writedate, rownum rn 
	from guestinput)
where rn between #{startRow} and #{endRow}
order by rn asc

select num, id, title, name, selectBoard, phone, email, reason, writedate, rn
from(select num, id, title, name, selectBoard, phone, email, reason, writedate, rownum rn 
	from guestinput)
where num=#{num}
